	function phoneVali(e){
		
		var number = e.replace(/[^0-9]/g, "");
    	var phone = "";
		
		if(number.length < 4) {
       		return number;
    	} else if(number.length < 7) {
        	phone += number.substr(0, 3);
        	phone += "-";
        	phone += number.substr(3);
    	} else if(number.length < 11) {
        	phone += number.substr(0, 3);
        	phone += "-";
        	phone += number.substr(3, 3);
        	phone += "-";
        	phone += number.substr(6);
    	} else {
        	phone += number.substr(0, 3);
        	phone += "-";
        	phone += number.substr(3, 4);
        	phone += "-";
        	phone += number.substr(7);
    	}
    		return phone;		
    }
    
    function regVali(e){
	    
	    var ContentsData = e;
		var len;
	
		console.log("aaaaaaaaaaaa:"+ContentsData);
	
		var ninArray = ContentsData.match(/(?:[0-9]{2}(?:0[1-9]|1[0-2])(?:0[1-9]|[1,2][0-9]|3[0,1]))-[1-4]{1}[0-9]{6}\b/gi);
    	
    	if(ninArray== null || ninArray =="")
    	{
        	ContentsData = ContentsData;
    	}
    	else
    	{
        	len = ninArray.toString().split('-').length;
        	ContentsData = ContentsData.toString().replace(ninArray,ninArray.toString().replace(/-?([1-4]{1})([0-9]{5})$/gi,"******"));
    	}

    	ninArray = ContentsData.match(/\d{13}/gi);
    	if(ninArray== null || ninArray =="")
    	{
        	ContentsData = ContentsData;
    	}
    	else
    	{
        	ContentsData = ContentsData.toString().replace(ninArray,ninArray.toString().replace(/([0-9]{6})$/gi,"******"));
   	 	}
    
    	console.log("ContentsData:"+ContentsData);
    	
    	return ContentsData;
	    
	}
	
	function cmpVali(e){
		companyNum = e.replace(/[^0-9]/g, '');
    	var tempNum = '';   

    	if(companyNum.length < 4){
      		return companyNum;
    	}
    	else if(companyNum.length < 6){
      		tempNum += companyNum.substr(0,3);
      		tempNum += '-';
      		tempNum += companyNum.substr(3,2);
      		return tempNum;
    	}
    	else if(companyNum.length < 11){
      		tempNum += companyNum.substr(0,3);
      		tempNum += '-';
      		tempNum += companyNum.substr(3,2);
      		tempNum += '-';
      		tempNum += companyNum.substr(5);
      		return tempNum;
    	}
    	else{        
      		tempNum += companyNum.substr(0,3);
      		tempNum += '-';
      		tempNum += companyNum.substr(3,2);
      		tempNum += '-';
      		tempNum += companyNum.substr(5);
      		return tempNum;
    	}
	}
	
	function multiUploadSelectDone(){
		var insa_cmp_name = cmp_up.getFileNames();
		
		insa_cmp_reg_image.setValue(insa_cmp_name[0]);
		
		console.log("insa_cmp_name[0]:"+insa_cmp_name[0]);
		
		var ev = window.event;
		var files = ev.target.files;
		fileRead(files);
		
	};
	
	function fileRead(tmpFile){
		if (tmpFile && tmpFile[0]) {
			var reader = new FileReader();
			reader.onload = function(e) {
				try{
					insa_cmp_reg_image_im.setSrc(e.target.result);
				}catch(ex){ 
				}finally{
					reader.onload = null;
					reader = null;
				}
			};
			reader.readAsDataURL(tmpFile[0]);
		}
	}
	
