	function emailVali(e){
		if(e == "@naver.com"){
			insa_email2.setValue("@naver.com");
		}else if(e == "@gmail.com"){
			insa_email2.setValue("@gmail.com");
		}else if(e == "@daum.net"){
			insa_email2.setValue("@daum.net");
		}else{
			insa_email2.setValue("직접입력");
		}
		return e;
	}
	
