	scwin.insa_mil_yn_onchange = function() {
		
		if(insa_mil_yn.getValue() == "H1" || insa_mil_yn.getValue() == "H2"){
			insa_mil_type.setReadOnly(true);
			insa_mil_level.setReadOnly(true);
			insa_mil_startdate.setReadOnly(true);
			insa_enddate.setReadOnly(true);
			
			insa_mil_type.setValue("");
			insa_mil_level.setValue("");
			insa_mil_startdate.setValue("");
			insa_enddate.setValue("");
			
			insa_mil_type.setStyle("background-color", "#D9D9D9");
			insa_mil_level.setStyle("background-color", "#D9D9D9");
			insa_mil_startdate.setStyle("background-color", "#D9D9D9");
			insa_enddate.setStyle("background-color", "#D9D9D9");
		}else{
			insa_mil_type.setReadOnly(false);
			insa_mil_level.setReadOnly(false);
			insa_mil_startdate.setReadOnly(false);
			insa_enddate.setReadOnly(false);
			
			insa_mil_type.setStyle("background-color", "white");
			insa_mil_level.setStyle("background-color", "white");
			insa_mil_startdate.setStyle("background-color", "white");
			insa_enddate.setStyle("background-color", "white");	
		}
		
	};
		
