	scwin.sub_update_submit = function(e) {
		//1.아이디 정규식 : 4 ~ 20 자리 영(대, 소), 숫자 / 첫글자는 숫자 사용 불가
		var idpattern = /^[A-Za-z]{1}[A-Za-z0-9]{3,19}$/;
		//2. 비밀번호 정규식 : 최소 8 자, 최소 하나의 문자 및 하나의 숫자
		var pwpattern = /^(?=.*[A-Za-z])(?=.*\d)[A-Za-z\d]{8,}$/;
		//3. 이메일 정규식 : 4 ~ 12 영(대, 소), 숫자
		var emailpattern = /^[A-Za-z0-9+]{4,12}$/;
		//3-2. 이메일 주소 정규식
		var emailpattern2 = /@[0-9a-zA-Z]([-_.]?[0-9a-zA-Z])*.[a-zA-Z]{2,3}$/i;
		//4. 숫자 정규식
		var numpattern =  /^[0-9]*$/;
		//5. 한글 정규식
		var hanpattern = /^[가-힣]+$/;
		//6. 전화번호 형태 : 전화번호 형태 000-0000-0000 만 받는다. ]
		var phonepattern = /^[0-9]{2,3}-[0-9]{3,4}-[0-9]{4}$/;
		//7. 주민번호, -까지 포함된 문자열로 검색
		var juminpattern = /^(?:[0-9]{2}(?:0[1-9]|1[0-2])(?:0[1-9]|[1,2][0-9]|3[0,1]))-[1-4][0-9]{6}$/;
		//8. 영문 정규식
		var engpattern = /^[a-zA-Z]*$/;
		//9. 숫자, 하이픈 정규식
		var numhapattern = /^[0-9-]{2,20}$/;
		
		if(insa_id.getValue()==""){
			alert("아이디를 입력해주세요");
			insa_id.focus();
			return false;
		}else if(pwpattern.test(insa_pwd.getValue())==false){
			alert("비밀번호는 최소 8 자, 최소 하나의 문자 및 하나의 숫자를 입력해주세요");
			insa_pwd.focus();
			return false;
		}else if(insa_pwd.getValue()!=insa_pwd2.getValue()){
			alert("비밀번호가 일치하지 않습니다. 다시 작성해주세요");
			insa_pwd2.focus();
			return false;
		}else if(insa_email1.getValue()==""){
			alert("이메일을 입력해주세요.");
			insa_email1.focus();
			return false;
		}else if(emailpattern.test(insa_email1.getValue())==false){
			alert("이메일 형식에 맞지 않는 이메일입니다. 영문과 숫자만 입력 가능합니다");
			insa_email1.focus();
			return false;
		}else if(emailpattern2.test(insa_email3.getValue())==false){
			alert("이메일 주소를 정확히 입력해주세요");
			insa_email3.focus();
			return false;
		}else if(insa_name.getValue()==""){
			alert("이름을 입력해주세요");
			insa_name.focus();
			return false;
		}else if(insa_hp.getValue()==""){
			alert("핸드폰번호를 입력해주세요");
			insa_hp.focus();
			return false;
		}else if(insa_join_day.getValue()==""){
			alert("입사일자를 선택해주세요");
			insa_join_day.focus();
			return false;
		}			

		if(!(insa_cmp_reg_image.getValue()=="")){
			console.log(insa_cmp_reg_image.getValue());
			
			var cnt = cmp_up.getFileCount();
			
			for(i=0;i<cnt;i++){
   			console.log("cnt:"+i);
   			cmp_up.setParam( i , "param"+i , "param"+i+"_value" );
  			}
			
			cmp_up.startUpload();
		}
		
		if(!(insa_carrier.getValue()=="")){
			console.log(insa_carrier.getValue());
			
			var cnt = car_up.getFileCount();
			
			for(i=0;i<cnt;i++){
   			console.log("cnt:"+i);
   			car_up.setParam( i , "param"+i , "param"+i+"_value" );
  			}
			
			car_up.startUpload();
		}
		
//		if(!(img_profile.getSrc()=="")){
//			console.log(img_profile.getSrc());
//			insa.set("INSA_PROFILE", img_profile.getSrc());
//		}
		
		return;
	};
